package com.codinghero.algorithm;

import junit.framework.Assert;

import org.junit.Test;

public class PalindromePartition2Test {
	
	@Test
	public void minCut() {
		Assert.assertEquals(0, new PalindromePartition2Optimize().minCut("abcba"));
		Assert.assertEquals(0, new PalindromePartition2Optimize().minCut("a"));
		//Assert.assertEquals(0, new PalindromePartition2Optimize().minCut(""));
		
		Assert.assertEquals(1, new PalindromePartition2Optimize().minCut("aab"));
		Assert.assertEquals(3, new PalindromePartition2Optimize().minCut("aabca"));
		new PalindromePartition2Optimize().minCut("");
	}
}
